import React from 'react'

export default function Cards(props) {

    let options = props.options;

    let priceoptions = Object.keys(options);
    let fooditem = props.fooditems;



    return (
        <div>
            <div class="card mt-3" style={{ 'width': '18rem', "maxeight": "360px" }}>
                <img src={props.fooditem.img} class="card-img-top" alt="FoodImage" style={{height:"160px",objectFit:"cover"}}/>
                <div class="card-body">
                    <h5 class="card-title">{props.foodName}</h5>
                    <p class="card-text">{props.desc}</p>
                    <div className='container w-100'>
                        <select className='m-2 h-100  bg-success'>
                            {Array.from(Array(6), (e, i) => {
                                return (
                                    <option key={i + 1} value={i + 1}>{i + 1}</option>
                                )
                            })}
                        </select>

                        <select className='m-2 h-100 bg-success rounded'>
                            {priceoptions.map((data)=>{
                                return (
                                    <option key={data} value={data}>{data}</option>
                                )
                            })}
                        </select>
                        <div className='d-inline h-100 fs-5'>Total price
                        </div>
                        <hr/>
                        <button className='btn btn-success'>Add to Cart</button>
                    </div>
                </div>
            </div>
        </div>
    )
}
