import React,{useState} from 'react'
import { Link } from 'react-router-dom'
export default function Signup() {
    const [Credentials,setcredentials] = useState({
        name:"",
        password:"",
        email:"",
        location:""
    })
    const update=(event)=>
    {
        setcredentials({...Credentials,[event.target.name]:event.target.value})
    }

    const handleSubmit = async (e)=>{
        e.preventDefault();
        const response = await fetch("http://localhost:5000/api/createuser",{

            method:"POST",
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify({
                name:Credential.name,
                email:Credential.email,
                password:Credential.password,
                location:Credential.location
            })
        }).then(res=>{
            return res.json();
        })
        const json = await response.json();
        console.log(json);
    
        if(!json.success){
            alert("Enter valid crendentials");
        }
    }
    return (
        <div>
            <div className='container'>
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="name" className="form-label">Name</label>
                        <input type="text" className="form-control" name="name" value={Credentials.name} onChange={update}/>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="email" className="form-label">Email address</label>
                        <input type="email" className="form-control" name="email" value={Credentials.email} onChange={update}/>
                        <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                    </div>

                    <div className="mb-3">
                        <label htmlFor="address" className="form-label">location</label>
                        <input type="text" className="form-control"  name="location" onChange={update} value={Credentials.location} />
                    </div>


                    <div className="mb-3">
                        <label htmlFor="password" className="form-label">Password</label>
                        <input type="password" className="form-control"  name="password" onChange={update} value={Credentials.password}/>
                    </div>
                    <button type="submit" className="btn btn-primary">Submit</button>
                    <Link to="/login" className='m-3 btn btn-danger'>Already a user</Link>
                </form>
            </div>
        </div>
    )
}
