import React,{useState} from 'react'
import { Link } from 'react-router-dom'

const handleSubmit = async (e)=>{
    e.preventDefault();
    const response = await fetch("http://localhost:5000/api/createuser",{
        method:"POST",
        header:{
            "Content-Type":"application/json"
        },
        body:JSON.stringify({
            name:Credential.name,
            email:Credential.email,
            password:Credential.password,
            location:Credential.location
        })
    })
    const json = await response.json();
    console.log(json);

    if(!json.success){
        alert("Enter valid crendentials");
    }
}

export default function Signup() {


    const [Credentials,setcredentials] = useState({
        name:"",
        password:"",
        email:"",
        location:""
    })

    const update=(event)=>
    {
        setcredentials({...Credentials,[event.target.name]:event.target.value})
    }

    return (
        <div>
            <div className='container'>
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label for="name" className="form-label">Username</label>
                        <input type="text" className="form-control" name="name" value={Credentials.name} onChange={update}/>
                    </div>
                    <div className="mb-3">
                        <label for="exampleInputEmail1" className="form-label">Email address</label>
                        <input type="email" className="form-control" name="email" value={Credentials.email} onChange={update} id="exampleInputEmail1" aria-describedby="emailHelp" />
                        <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                    </div>

                    <div className="mb-3">
                        <label for="address" className="form-label">location</label>
                        <input type="text" className="form-control"  name="location " onChange={update} value={Credentials.location} />
                    </div>


                    <div className="mb-3">
                        <label for="exampleInputPassword1" className="form-label">Password</label>
                        <input type="password" className="form-control"  name="password" onChange={update} value={Credentials.password} id="exampleInputPassword1" />
                    </div>

                    <div className="mb-3 form-check">
                        <input type="checkbox" className="form-check-input"  id="exampleCheck1" />
                        <label className="form-check-label" for="exampleCheck1">Check me out</label>
                    </div>
                    <button type="submit" className="btn btn-primary">Submit</button>
                    <Link to="/login" className='m-3 btn btn-danger'>Already a user</Link>
                </form>
            </div>
        </div>
    )
}
